import React from 'react';
import logo from './logo.svg';
import './App.css';
import ApolloClient, {gql} from 'apollo-boost';
import { ApolloProvider, useMutation } from '@apollo/react-hooks';

const UPLOAD_DOCUMENT = gql`
  mutation UploadDocument($documents: [Upload]){
    createLawfirmDocument (input: {data: {Documents: $documents}}){
      lawfirmDocument {
        Documents {
          name
        }
      }
    }
  }
`

const client = new ApolloClient({
  uri: 'http://localhost:1337/graphql',
});

function UploadDoc() {
  const [files, setFiles] = React.useState(null)
  const [uploadDocument, {data, loading , error}] = useMutation(UPLOAD_DOCUMENT)
  return (
    <div className="App">
      <input type="file" onChange={e => {
        console.log(e.target.files)
        // const newFiles = e.target.files.map(file => file)
        // setFiles(newFiles)
      }}></input>
    </div>
  );
}

function App() {
    return (
    <ApolloProvider client={client}>
    <UploadDoc />
    </ApolloProvider>
  );
} 

export default App;
